	i = id.substring(0, 2);
    const a = document.querySelectorAll('h1, h2, h3, h4, h5, span');
    a.forEach((e) => { e.innerHTML = tExp(e.innerHTML, i); });

	nombre.placeholder 		= tExp(nombre.placeholder, 	i);
	dirCorreo.placeholder 	= tExp(dirCorreo.placeholder, 	i);
	asunto.placeholder 		= tExp(asunto.placeholder, 	i);
	mensaje.placeholder 	= tExp(mensaje.placeholder, i);

	if 		(i == 'es') 	{ enviar.value = "Subscribirme";		msjPolPriv = "Debe leer y aceptar la Política de Privacidad para poder enviar el formulario"; }
