function tExp (s, i) {
    var p1 = exp1_es.indexOf(s);
    if (p1 == -1) {
        var p2 = exp2_es.indexOf(s);
        if (p2 == -1) {
            return s;
        } else {
	    	if 		(i == 'es') { return exp2_es[p2]; }
	    	else if (i == 'en') { return exp2_en[p2]; }
	    	else if (i == 'fr') { return exp2_fr[p2]; }
	    	else if (i == 'pt') { return exp2_pt[p2]; }
	    	else if (i == 'it') { return exp2_it[p2]; } 
	    	else if (i == 'de') { return exp2_de[p2]; } 
	    	else if (i == 'ca') { return exp2_ca[p2]; } 
	    	else if (i == 'gl') { return exp2_gl[p2]; } 
	    	else if (i == 'eu') { return exp2_eu[p2]; } 
        }
    } else {
    	if 		(i == 'es') { return exp1_es[p1]; } 
    	else if (i == 'en') { return exp1_en[p1]; } 
    	else if (i == 'fr') { return exp1_fr[p1]; } 
    	else if (i == 'pt') { return exp1_pt[p1]; }
    	else if (i == 'it') { return exp1_it[p1]; }
    	else if (i == 'de') { return exp1_de[p1]; } 
    	else if (i == 'ca') { return exp1_ca[p1]; }
    	else if (i == 'gl') { return exp1_gl[p1]; }
    	else if (i == 'eu') { return exp1_eu[p1]; }
    }
}


